import React, { useState } from "react";

function Createarea(props) {

    const [inputnote, setinputnote]=useState({
        title: "",
        content: "",
    });

    function handlechange(event){
        const {name, value} = event.target;
        setinputnote((prevNote)=>{
            return {
                ...prevNote,
                [name]: value,
            }
        })
    }

    function handleclick(){};

  return (
    <div>
      <form>
        <input name="title" type="text" placeholder="This is a note title" value={inputnote.title}/>
        <textarea name="content" placeholder="Take a note" value={inputnote.content} onChange={handlechange} rows="3"/>
        <button onClick={handleclick}>Add</button>
      </form>
    </div>
  );
}

export default Createarea;
